figure.artwork:
Generate CEK
for i=1 to n {
   Fetch KEK_i(Ri, S)
   ENC(CEK, KEK_i)
}
ENC(payload,CEK)

for i=1 to n {
   Fetch KEK_i(Ri, S)
   Generate CEK_i
   ENC(CEK_i, KEK_i)
   ENC(payload,CEK_i)
}

COSE_Encrypt_Tagged = #6.96(COSE_Encrypt)
SUIT_Encryption_Info = COSE_Encrypt_Tagged
COSE_Encrypt = [ protected : bstr .cbor outer_header_map_protected, unprotected : outer_header_map_unprotected, ciphertext : bstr / nil, recipients : [ + COSE_recipient ] ]
outer_header_map_protected = { 1 => int, ; algorithm identifier * label =values ; extension point }
outer_header_map_unprotected = { 5 => bstr, ; IV * label =values ; extension point }
COSE_recipient = [ protected : bstr .size 0, unprotected : recipient_header_map, ciphertext : bstr ; CEK encrypted with KEK ]
recipient_header_map = { 1 => int, ; algorithm identifier 4 => bstr, ; key identifier * label =values ; extension point }

Generate CEK
for i=1 to n {
   Generate KEK_i(Ri, S) using ES-DH
   ENC(CEK, KEK_i)
}
ENC(payload,CEK)

for i=1 to n {
   Generate KEK_i(Ri, S) using ES-DH
   Generate CEK_i
   ENC(CEK_i, KEK_i)
   ENC(payload,CEK_i)
}

COSE_Encrypt_Tagged = #6.96(COSE_Encrypt)
SUIT_Encryption_Info = COSE_Encrypt_Tagged
COSE_Encrypt = [ protected : bstr .cbor outer_header_map_protected, unprotected : outer_header_map_unprotected, ciphertext : bstr / nil, recipients : [ + COSE_recipient ] ]
outer_header_map_protected = { 1 => int, ; algorithm identifier * label =values ; extension point }
outer_header_map_unprotected = { 5 => bstr, ; IV * label =values ; extension point }
COSE_recipient = [ protected : bstr .size 0, unprotected : recipient_header_map, ciphertext : bstr ; CEK encrypted with KEK recipients : [ + COSE_recipient_inner ] ]
recipient_header_map = { 1 => int, ; algorithm identifier for key wrap 4 => bstr, ; key identifier * label =values ; extension point }
COSE_recipient_inner = [ protected : bstr .cbor inner_recipient_header_pr_map, unprotected : inner_recipient_header_unpr_map, ciphertext : nil ]
inner_recipient_header_pr_map = { 1 => int, ; algorithm identifier for ES-DH * label =values ; extension point }
inner_recipient_header_unpr_map = { 1 => int, ; algorithm identifier -1 => COSE_Key, ; ephemeral public key for the sender 4 => bstr, ; identifier of the recipient public key * label =values ; extension point }

